mapper.select:
select task_id, writer_id, writer_name, team_id, content
 from task_tb
  where team_id = #{teamId}

select task_id, writer_id, writer_name, team_id, content
 from task_tb
  where task_id = #{taskId}

mapper.update:
update task_tb
 set
  content = #{content}
   where task_id = #{taskId}
